// First cut at monitoring websites from a web page application
//

import { LogToWindow, consolelog, rgb2hex } from "./utilities.js";

const CheckInterval = 30 * 1000; // msec
const requestTimeout = 5 * 1000; // msec
const spinnerTimeout = 3 * 1000; // msec

const headers = document.getElementsByTagName("header");
headers[0].onclick = () => {
  LogToWindow("Manual check...");
  consolelog("Manual check...");
  CheckHosts();
};

// We're starting up
LogToWindow("Starting TestMyInter.net - Leave the window open");
consolelog("Starting test");

CheckHosts();         // kick off the test

// queue up a test of all hosts every now and again
setInterval (CheckHosts, CheckInterval);    // every 30 sec

// find all the <host> elements on the page, iterate through them

function CheckHosts() {

  const spinner = document.getElementById("spinner");
  spinner.style.visibility = "visible";
  setTimeout(() => { spinner.style.visibility = "hidden"; }, spinnerTimeout);

  const hostList = document.getElementsByTagName("host");
  for (let i = 0; i < hostList.length; i++) {
    CheckHost(hostList[i]);
  }
}

// CheckHost - aHost is a DOM element; get its text, then set the background color

function CheckHost(aHost) {

  const hostName = aHost.innerHTML;

  CheckAlive(hostName + ":80")
    .then(
      (msg) => UpdateDevice(aHost, msg, "Success"),
      (err) => UpdateDevice(aHost, err, "Timeout"))
    .catch((err) => UpdateDevice(aHost, err, "Catch"));
}

function UpdateDevice(aHost, text, status) {

  let color;
  let curColor = aHost.style.backgroundColor;
  if (curColor) {
    curColor = rgb2hex(curColor);
  }
  const hostName = aHost.innerHTML;
  if      (text.indexOf("OK:") === 0)    color = "#4caf50"; // material.io 500 green
  else if (text.indexOf("Abort:") === 0) color = "#ffeb3b"; // material.io 500 yellow
  else if (text.indexOf("Down:") === 0)  color = "#f44336"; // material.io 500 red
  else color = "#9c27b0";                                   // material.io 500 purple
  aHost.style.backgroundColor = color;
  if (color !== curColor) {
    LogToWindow(text + aHost.innerHTML);
    beep2();
 }
  // if (hostName === "192.168.0.1" && color === "green") {
  // }
  consolelog(`${hostName} returned: "${text}", ${status}`);
}

// Play a short beep
// http://stackoverflow.com/questions/879152/how-do-i-make-javascript-beep
function beep2()
{ const snd = new Audio("data:audio/wav;base64,");
  snd.play();
}

// Check For Liveness - send a http query to the named host and port
// Detect whether it timed out (non-responsive) or returned some other
//    response/error (most likely will give CORS error, which also means it's up)
// See also: https://italonascimento.github.io/applying-a-timeout-to-your-promises/

function CheckAlive(host_port) {

  const req = new XMLHttpRequest();             // this is the request we'll use to test host_port

  // promise to run the timer that checks for a connection
  const timerPromise = new Promise((resolve, reject) => {
    let id = setTimeout(() => {
      clearTimeout(id);
      req.abort();                              // abort the reqPromise so it doesn't re-surface later
      reject('Down: timeout ');
    }, requestTimeout);
  });

  // promise to attempt to connect - it never times out on its own
  const reqPromise = new Promise(function (resolve, reject) {
    const myurl = encodeURI('http://TestMyInter.net/');
    const url = "http://" + host_port + "/?page="+ myurl;

    req.addEventListener("load"   , () => { resolve('OK:    ') });    // Got some kind of response back
    req.addEventListener("error"  , () => { resolve('OK:    ') });    // CORS errors show the server is there
    // req.addEventListener("abort"  , () => { reject ('Abort: ') });    // Somebody aborted test (don't know how)
    // req.addEventListener("timeout", () => { reject ('Down:  ') });    // timed out - presumably couldn't connect

    // LogToWindow("Testing " + url);
    req.open('GET', url, true);                         // async http GET from host
    req.timeout = 0;                                    // Don't let XMLHttpRequest time out on its own
    // req.ontimeout = (e) => { reject ('Down:  ') };
    req.send();
  });

  // Returns a race between our timeout and request promises
  return Promise.race([
    timerPromise,
    reqPromise
  ]);
}
